import React, { useState, useEffect } from "react";

function Materias() {
  const [materias, setMaterias] = useState([]);
  const [form, setForm] = useState({ nombre: "", creditos: "" });

  useEffect(() => {
    cargarMaterias();
  }, []);

  const cargarMaterias = async () => {
    const res = await fetch("http://localhost:3000/materias");
    setMaterias(await res.json());
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:3000/materias", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    setForm({ nombre: "", creditos: "" });
    cargarMaterias();
    alert("Materia creada");
  };

  return (
    <div>
      <h2>Gestión de Materias</h2>
      <div className="card">
        <form onSubmit={handleSubmit}>
          <label>Nombre de la Materia:</label>
          <input
            value={form.nombre}
            onChange={(e) => setForm({...form, nombre: e.target.value})}
            required
          />
          <label>Créditos:</label>
          <input
            type="number"
            value={form.creditos}
            onChange={(e) => setForm({...form, creditos: e.target.value})}
            required
          />
          <button type="submit" style={{backgroundColor: "#4299e1"}}>Crear Materia</button>
        </form>
      </div>

      <div className="lista" style={{marginTop: "20px"}}>
        <h3>Materias Disponibles</h3>
        <ul>
            {materias.map(mat => (
                <li key={mat.id} style={{padding: "10px", borderBottom: "1px solid #eee"}}>
                    {mat.nombre} - {mat.creditos} créditos
                </li>
            ))}
        </ul>
      </div>
    </div>
  );
}

export default Materias;
