import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [estudiantes, setEstudiantes] = useState([]);
  const [formulario, setFormulario] = useState({ nombre: "", email: "" });

  // 1. Cargar estudiantes al iniciar
  useEffect(() => {
    cargarEstudiantes();
  }, []);

  const cargarEstudiantes = async () => {
    try {
      const response = await fetch("http://localhost:3000/estudiantes");
      const data = await response.json();
      setEstudiantes(data);
    } catch (error) {
      console.error("Error cargando estudiantes:", error);
    }
  };

  // 2. Manejar cambios en el formulario
  const handleChange = (e) => {
    setFormulario({ ...formulario, [e.target.name]: e.target.value });
  };

  // 3. Enviar datos al Backend
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await fetch("http://localhost:3000/estudiantes", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formulario),
      });
      // Limpiar formulario y recargar lista
      setFormulario({ nombre: "", email: "" });
      cargarEstudiantes();
      alert("Â¡Estudiante registrado con Ã©xito!");
    } catch (error) {
      console.error("Error registrando:", error);
    }
  };

  return (
    <div className="container">
      <h1>ðŸŽ“ GestiÃ³n Escolar</h1>

      {/* Formulario de Registro */}
      <div className="card">
        <h2>Registrar Estudiante</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="nombre"
            placeholder="Nombre completo"
            value={formulario.nombre}
            onChange={handleChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Correo electrÃ³nico"
            value={formulario.email}
            onChange={handleChange}
            required
          />
          <button type="submit">Guardar Estudiante</button>
        </form>
      </div>

      {/* Lista de Estudiantes */}
      <div className="lista">
        <h2>Estudiantes Registrados ({estudiantes.length})</h2>
        {estudiantes.length === 0 ? (
          <p>No hay estudiantes registrados aÃºn.</p>
        ) : (
          <ul>
            {estudiantes.map((est) => (
              <li key={est.id} className="item-estudiante">
                <strong>{est.nombre}</strong> - {est.email}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default App;
