import React, { useState } from "react";

function Login({ alIngresar }) {
  const [usuario, setUsuario] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    
    try {
      // Petición al Backend en el puerto 3000
      const response = await fetch("http://localhost:3000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username: usuario, password: password }),
      });

      const data = await response.json();

      if (data.exito) {
        // Si el login es correcto, avisamos a App.js
        alIngresar(data.usuario);
      } else {
        // Si falla, mostramos mensaje de error
        setError(true);
      }
    } catch (err) {
      console.error("Error de conexión", err);
      setError(true);
    }
  };

  // Estilos visuales para esta página
  const estilos = {
    fondo: {
      height: "100vh",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      backgroundColor: "#f0f2f5",
      backgroundImage: "linear-gradient(135deg, #0044cc 0%, #002a80 100%)",
    },
    tarjeta: {
      background: "white",
      padding: "40px",
      borderRadius: "10px",
      boxShadow: "0 10px 25px rgba(0,0,0,0.2)",
      width: "100%",
      maxWidth: "400px",
      textAlign: "center",
    },
    input: {
      width: "100%",
      padding: "12px",
      margin: "10px 0",
      border: "1px solid #ccc",
      borderRadius: "5px",
      fontSize: "16px",
    },
    boton: {
      width: "100%",
      padding: "12px",
      backgroundColor: "#0044cc",
      color: "white",
      border: "none",
      borderRadius: "5px",
      fontSize: "16px",
      cursor: "pointer",
      marginTop: "10px",
      fontWeight: "bold",
      transition: "background 0.3s",
    },
    error: {
      color: "#dc3545",
      fontSize: "14px",
      marginTop: "15px",
      fontWeight: "bold",
      backgroundColor: "#f8d7da",
      padding: "10px",
      borderRadius: "5px",
      border: "1px solid #f5c6cb"
    }
  };

  return (
    <div style={estilos.fondo}>
      <div style={estilos.tarjeta}>
        <h2 style={{color: "#0044cc", marginBottom: "5px"}}>Bienvenido</h2>
        <p style={{color: "#666", marginBottom: "20px"}}>Sistema de Gestión Académica</p>
        
        <form onSubmit={handleLogin}>
          <input 
            type="text" 
            placeholder="Usuario (Ej: Admin)" 
            value={usuario}
            onChange={(e) => setUsuario(e.target.value)}
            style={estilos.input}
            required
          />
          <input 
            type="password" 
            placeholder="Contraseña" 
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={estilos.input}
            required
          />
          
          <button type="submit" style={estilos.boton}>
            Iniciar Sesión
          </button>
        </form>

        {error && (
          <div style={estilos.error}>
            ⚠ Usuario o contraseña incorrectos
          </div>
        )}
        
        <div style={{marginTop: "30px", fontSize: "12px", color: "#aaa"}}>
          Universidad Prototipo © 2026<br/>
          Todos los derechos reservados.
        </div>
      </div>
    </div>
  );
}

export default Login;
