import React, { useState, useEffect } from "react";

function Estudiantes() {
  const [estudiantes, setEstudiantes] = useState([]);
  const [form, setForm] = useState({ nombre: "", email: "" });

  useEffect(() => {
    cargarEstudiantes();
  }, []);

  const cargarEstudiantes = async () => {
    const res = await fetch("http://localhost:3000/estudiantes");
    setEstudiantes(await res.json());
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:3000/estudiantes", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    setForm({ nombre: "", email: "" });
    cargarEstudiantes();
    alert("Estudiante registrado");
  };

  return (
    <div>
      <h2>Gesti√≥n de Estudiantes</h2>
      <div className="card">
        <form onSubmit={handleSubmit}>
          <label>Nombre Completo:</label>
          <input
            value={form.nombre}
            onChange={(e) => setForm({...form, nombre: e.target.value})}
            required
          />
          <label>Email:</label>
          <input
            value={form.email}
            onChange={(e) => setForm({...form, email: e.target.value})}
            required
          />
          <button type="submit">Guardar Estudiante</button>
        </form>
      </div>

      <div className="lista" style={{marginTop: "20px"}}>
        <h3>Lista de Estudiantes</h3>
        <ul>
            {estudiantes.map(est => (
                <li key={est.id} style={{padding: "10px", borderBottom: "1px solid #eee"}}>
                    {est.nombre} ({est.email})
                </li>
            ))}
        </ul>
      </div>
    </div>
  );
}

export default Estudiantes;
