import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import "./App.css";

// Importamos nuestros componentes
import Navbar from "./Navbar";
import Estudiantes from "./paginas/estudiantes";
import Materias from "./paginas/Materias";
import Calificaciones from "./paginas/Calificaciones";

function App() {
  return (
    <Router>
      <div className="container">
        <h1>Sistema de Gestión Escolar</h1>

        {/* Aquí ponemos el menú que se verá siempre */}
        <Navbar />

        {/* Aquí definimos qué componente carga en cada ruta */}
        <Routes>
          <Route path="/" element={
            <div style={{textAlign: "center"}}>
              <h2>¡Bienvenido al Panel de Control!</h2>
              <p>Selecciona una opción del menú para comenzar.</p>
            </div>
          } />
          <Route path="/estudiantes" element={<Estudiantes />} />
          <Route path="/materias" element={<Materias />} />
          <Route path="/calificaciones" element={<Calificaciones />} />
        </Routes>

      </div>
    </Router>
  );
}

export default App;
