import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  // --- ESTADOS (La memoria del frontend) ---
  const [estudiantes, setEstudiantes] = useState([]);
  const [materias, setMaterias] = useState([]);
  const [calificaciones, setCalificaciones] = useState([]);

  // --- FORMULARIOS ---
  const [formEstudiante, setFormEstudiante] = useState({ nombre: "", email: "" });
  const [formMateria, setFormMateria] = useState({ nombre: "", creditos: "" });
  const [formCalificacion, setFormCalificacion] = useState({ estudiante_id: "", materia_id: "", nota: "" });

  // --- CARGA INICIAL DE DATOS ---
  useEffect(() => {
    cargarDatos();
  }, []);

  const cargarDatos = async () => {
    try {
      const resEst = await fetch("http://localhost:3000/estudiantes");
      const resMat = await fetch("http://localhost:3000/materias");
      const resCalif = await fetch("http://localhost:3000/calificaciones");

      setEstudiantes(await resEst.json());
      setMaterias(await resMat.json());
      setCalificaciones(await resCalif.json());
    } catch (error) {
      console.error("Error cargando datos:", error);
    }
  };

  // --- MANEJADORES DE ENVÃO (Submit) ---

  // 1. Registrar Estudiante
  const handleEstudianteSubmit = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:3000/estudiantes", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formEstudiante),
    });
    setFormEstudiante({ nombre: "", email: "" }); // Limpiar formulario
    cargarDatos(); // Recargar listas
    alert("Estudiante registrado");
  };

  // 2. Crear Materia
  const handleMateriaSubmit = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:3000/materias", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formMateria),
    });
    setFormMateria({ nombre: "", creditos: "" });
    cargarDatos();
    alert("Materia creada");
  };

  // 3. Asignar Nota
  const handleCalificacionSubmit = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:3000/calificaciones", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formCalificacion),
    });
    setFormCalificacion({ estudiante_id: "", materia_id: "", nota: "" });
    cargarDatos();
    alert("Nota asignada correctamente");
  };

  return (
    <div className="container">
      <h1>ðŸŽ“ Sistema de GestiÃ³n Escolar</h1>

      <div className="grid-layout">

        {/* COLUMNA 1: REGISTROS */}
        <div className="column">

          {/* Formulario Estudiantes */}
          <div className="card">
            <h2>1. Nuevo Estudiante</h2>
            <form onSubmit={handleEstudianteSubmit}>
              <input
                placeholder="Nombre completo"
                value={formEstudiante.nombre}
                onChange={(e) => setFormEstudiante({...formEstudiante, nombre: e.target.value})}
                required
              />
              <input
                placeholder="Email"
                value={formEstudiante.email}
                onChange={(e) => setFormEstudiante({...formEstudiante, email: e.target.value})}
                required
              />
              <button type="submit">Guardar Estudiante</button>
            </form>
          </div>

          {/* Formulario Materias */}
          <div className="card">
            <h2>2. Nueva Materia</h2>
            <form onSubmit={handleMateriaSubmit}>
              <input
                placeholder="Nombre materia (Ej. MatemÃ¡ticas)"
                value={formMateria.nombre}
                onChange={(e) => setFormMateria({...formMateria, nombre: e.target.value})}
                required
              />
              <input
                type="number"
                placeholder="CrÃ©ditos (Ej. 4)"
                value={formMateria.creditos}
                onChange={(e) => setFormMateria({...formMateria, creditos: e.target.value})}
                required
              />
              <button type="submit" style={{backgroundColor: "#007bff"}}>Crear Materia</button>
            </form>
          </div>
        </div>

        {/* COLUMNA 2: ASIGNAR NOTAS */}
        <div className="column">
          <div className="card highlight">
            <h2>3. Asignar CalificaciÃ³n</h2>
            <form onSubmit={handleCalificacionSubmit}>
              <label>Selecciona Estudiante:</label>
              <select 
                required 
                value={formCalificacion.estudiante_id}
                onChange={(e) => setFormCalificacion({...formCalificacion, estudiante_id: e.target.value})}
              >
                <option value="">-- Seleccionar --</option>
                {estudiantes.map(est => (
                  <option key={est.id} value={est.id}>{est.nombre}</option>
                ))}
              </select>

              <label>Selecciona Materia:</label>
              <select 
                required
                value={formCalificacion.materia_id}
                onChange={(e) => setFormCalificacion({...formCalificacion, materia_id: e.target.value})}
              >
                <option value="">-- Seleccionar --</option>
                {materias.map(mat => (
                  <option key={mat.id} value={mat.id}>{mat.nombre}</option>
                ))}
              </select>

              <label>Nota (0-100):</label>
              <input
                type="number"
                min="0" max="100"
                value={formCalificacion.nota}
                onChange={(e) => setFormCalificacion({...formCalificacion, nota: e.target.value})}
                required
              />
              <button type="submit" style={{backgroundColor: "#ffc107", color: "black"}}>Guardar Nota</button>
            </form>
          </div>
        </div>

      </div>

      {/* TABLA DE RESULTADOS */}
      <div className="lista">
        <h2>ðŸ“Š Historial de Calificaciones</h2>
        {calificaciones.length === 0 ? <p>No hay notas registradas.</p> : (
          <table style={{width: "100%", borderCollapse: "collapse"}}>
            <thead>
              <tr style={{background: "#ddd", textAlign: "left"}}>
                <th style={{padding: "10px"}}>Estudiante</th>
                <th style={{padding: "10px"}}>Materia</th>
                <th style={{padding: "10px"}}>Nota</th>
                <th style={{padding: "10px"}}>Fecha</th>
              </tr>
            </thead>
            <tbody>
              {calificaciones.map((cal) => (
                <tr key={cal.id} style={{borderBottom: "1px solid #eee"}}>
                  <td style={{padding: "10px"}}>{cal.estudiante}</td>
                  <td style={{padding: "10px"}}>{cal.materia}</td>
                  <td style={{padding: "10px", fontWeight: "bold"}}>{cal.nota}</td>
                  <td style={{padding: "10px"}}>{cal.fecha_evaluacion.split("T")[0]}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default App;
